if (flow.get('newSetting') == undefined) return null;
var ib;
var device = flow.get('device');
var dataView     = new DataView(new ArrayBuffer(4));
var newSettingDone = 0;
var cubeInit = 0;

for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
cubeInit = dataView.getInt32(0);
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[1 * 4 + 3 - ib]);
newSettingDone = dataView.getInt32(0);

var newMsg = {topic:'userData', payload:[]};
for (var ii = 8; ii < msg.payload.length; ++ii) newMsg.payload[ii - 8] = msg.payload[ii];

if (newSettingDone > 0) 
{
    flow.set('newSetting', 0);
}
if(cubeInit == 1) 
{
    flow.set('newSetting', 1);
}

return newMsg;

if (!msg.payload) return null;
var device = flow.get('device');
device.linkQuality.value     = global.get('linkQuality');
device.signalLevel.value     = global.get('signalLevel');
scalarAlarmScan(device, 'reading');
var newMsg = 
{
    topic: flow.get('deviceProps').project + '/' + flow.get('deviceProps').deviceType + '/' + flow.get('deviceProps').name + '/reading',
    payload: JSON.stringify(device)
}
return newMsg;

function getActionArray(device, type, actionType)
{
    var actionArray = [];
    var keys = Object.keys(device);
    for (var ii = 0; ii < keys.length; ++ii)
    {
        if (device[keys[ii]].hasOwnProperty('type'))
        {
            if (device[keys[ii]].type == type)
            {
                if (device[keys[ii]].action == actionType)
                {
                    actionArray.push(device[keys[ii]]);
                }
            }
        }
    }
    return actionArray;
}
function scalarAlarmScan(device, actionType)
{
    var actionArray = getActionArray(device, 'scalar', actionType);
    if (actionArray.length > 0)
    {
        for (var ii = 0; ii < actionArray.length; ++ii)
        {
            actionArray[ii].alarm.value = 0;
            if (!isNaN(actionArray[ii].value))
            {
                if (actionArray[ii].value < actionArray[ii].alarm.limits.low)  actionArray[ii].alarm.value = 1;
                if (actionArray[ii].value < actionArray[ii].alarm.limits.lolo) actionArray[ii].alarm.value = 3;
                if (actionArray[ii].value > actionArray[ii].alarm.limits.high) actionArray[ii].alarm.value = 2;
                if (actionArray[ii].value > actionArray[ii].alarm.limits.hihi) actionArray[ii].alarm.value = 4;
            }
        }
    }
}

var mqttArray = msg.topic.split('/');
var device = 
{
    project     : mqttArray[0],
    deviceType  : mqttArray[1],
    name        : mqttArray[2],
    prop        : mqttArray[3],
};
return {topic : msg.topic, payload:{topic:'readings', mqttDevice : device, payload:msg.payload}};

var ib;
var device = flow.get('device');
var dataView     = new DataView(new ArrayBuffer(4));

for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
device.mAX31855_A.value = Math.round(dataView.getFloat32(0) * 100) / 100;
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[1 * 4 + 3 - ib]);
device.mAX31855_B.value = Math.round(dataView.getFloat32(0) * 100) / 100;
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[2 * 4 + 3 - ib]);
device.dS18B20_A.value = Math.round(dataView.getFloat32(0) * 100) / 100;
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[3 * 4 + 3 - ib]);
device.dS18B20_B.value = Math.round(dataView.getFloat32(0) * 100) / 100;
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[4 * 4 + 3 - ib]);
var checkSum = dataView.getInt32(0);
if (checkSum != 20)
{
    node.warn("checkSum = " + checkSum.toString());
}

var newSettingDoneMsg = {topic:'newSettingDone', payload:true} 

return newSettingDoneMsg;

