return msg;

var ipfilter = [
    "127.0.0.16"];
var ipAddress = ( msg.req.headers['x-forwarded-for'] || '').split(',').pop() || 
          msg.req.connection.remoteAddress || 
          msg.req.socket.remoteAddress || 
          msg.req.connection.socket.remoteAddress;
if (ipAddress==undefined) return null;
var ipAddressSplit = ipAddress.split(".");
for (var ii =  0; ii < ipfilter.length; ++ii)
{
    var ipfilterSplit = ipfilter[ii].split(".");
    if (ipAddress == ipfilter[ii])
    { 
        return null;
    }
    if (ipfilterSplit[2] == '*')
    {
        if ((ipfilterSplit[0] == ipAddressSplit[0]) && (ipfilterSplit[1] == ipAddressSplit[1]))
        {
            return null;
        }
    }
}


return msg;

