// StripChart Widget
        class StripChart
        {
           constructor(plotConfig) 
            {
                this.plotConfig = plotConfig;
                this.plotStartTime = new Date().getTime();
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : plotConfig.height,
                    width       : plotConfig.width,
                    xaxis       :
                    {
//                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                        type: 'date'
                    },
                    yaxis:
                    {
                        title           : plotConfig.trace1Title,
                        titlefont       : {color: plotConfig.trace1Color},
                        tickfont        : {color:plotConfig.trace1Color},
                        gridcolor       : plotConfig.trace1GridColor,
                        zerolinecolor   : plotConfig.trace1GridColor,
                        linecolor       : plotConfig.trace1GridColor,
//                        range: [0, 2]

                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: 'MAX31855 A',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: plotConfig.trace1Color},
                    visible: true

                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'MAX31855 B',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: plotConfig.trace2Color},
                    visible:  true
                };
                this.trace3 = 
                {
                    x: [],
                    y: [],
                    name: 'DS18B20 A',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red'},
                    visible: true
                };
                this.trace4 = 
                {
                    x: [],
                    y: [],
                    name: 'DS18B20 B',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green'},
                    visible: true
                };
            }
            addTracePoints(trace1Pt, trace2Pt, trace3Pt, trace4Pt)
            {
//                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                        this.trace3.x.shift();
                        this.trace3.y.shift();
                        this.trace4.x.shift();
                        this.trace4.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                this.trace3.x.push(now);
                this.trace3.y.push(trace3Pt);
                this.trace4.x.push(now);
                this.trace4.y.push(trace4Pt);
                this.trace1.visible = $('#max31855ACheck').prop("checked");
                this.trace2.visible = $('#max31855BCheck').prop("checked");
                this.trace3.visible = $('#ds18b20ACheck').prop("checked");
                this.trace4.visible = $('#ds18b20BCheck').prop("checked");
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2, this.trace3, this.trace4], this.layout);
            }
        }

// Global Javascript
