var device = flow.get('device');
var temperature = device.temperature.value; 
var targetTemperature = temperature;
var state = context.get('state');
if (state == undefined) 
{
    state = 0;
    context.set('state',state);
}
var deviceState = device.regState.value; 
if (deviceState != state)
{
    state = deviceState;
    context.set('state',deviceState);
    context.set('startStateTime', new Date().getTime());
    context.set('startStateTemperature', temperature);
}
var now = msg.payload;
var rampUptime = device.rampUpTime.value; 
var regTemp = device.regTemp.value; 
var oldRampUpTime = context.get('oldRampUpTime');
var oldRegTemp = context.get('oldRegTemp');
if (oldRampUpTime == undefined) 
{
    oldRampUpTime = rampUptime;
    context.set('oldRampUpTime',oldRampUpTime);
}
if (oldRegTemp == undefined) 
{
    oldRegTemp = regTemp;
    context.set('oldRegTemp',oldRegTemp);
}
if ((oldRampUpTime != rampUptime) || (oldRegTemp != regTemp))
{
    context.set('startStateTime', new Date().getTime());
    context.set('startStateTemperature', temperature);
    context.set('oldRampUpTime',rampUptime);
    context.set('oldRegTemp',regTemp);
}

switch(state) 
{
  case 0:
    // off
    break;
  case 1:
    // ramp up
    var deltaTime = (now - context.get('startStateTime')) / 3600.0 / 1000.0;
    if (deltaTime > rampUptime) deltaTime = rampUptime;
    targetTemperature = (regTemp - context.get('startStateTemperature')) ;
    targetTemperature = targetTemperature * deltaTime / rampUptime;
    targetTemperature = targetTemperature + context.get('startStateTemperature');
    targetTemperature = Math.round(targetTemperature * 100.0) / 100.0;
    break;
  case 2:
    // on
    break;
  default:
    break;
}
device.rampTemp.value = targetTemperature;
flow.set('newSetting', 1);
return null;

msg.payload['script'] = {};
msg.payload['script']['name'] = msg.payload.name
msg.payload['script']['deviceType'] = 'blinky-ssr-bake';
msg.payload['script']['project'] = global.get('project');
return msg;

