        function arcPeriod()
        {
            $('#arcPeriodButton').hide();
            if (isNaN($('#arcPeriod').val()))
            {
                arcPeriodChanged = false;
                return;
            }
            device.arcPeriod = Math.round(Number($('#arcPeriod').val()) * 1000);
            sendSetting();
            arcPeriodChanged = false;
        }


        function changeState(state)
        {
            device.regState.value = state;
            sendSetting();
            changingState = true;
            $('#offStateButton').css("background-color", "grey");
            $('#rampUpStateButton').css("background-color", "grey");
            $('#rampDownStateButton').css("background-color", "grey");
            $('#regulateStateButton').css("background-color", "grey");
            $('#onStateButton').css("background-color", "grey");
            setTimeout(function(){ changingState = false; }, 1000);
        }
